using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Threading.Tasks;

namespace ConsoleApp3
{
    public class Decoder
    {
        //外面來的
        int chs = 188;
        int ti = 14132;
        string cs = "";
        int ch = 1;  //從網址來的


        int p = 1;
        int f = 50;
        string c = string.Empty;  //string.Empty 跨OS
        int ci = 0;


        public string sp()
        {
            var cc = cs.Length;
            for (var i = 0; i < cc / f; i++)
            {
                if (ss(cs, i * f, 4) == ch.ToString())
                {
                    c = ss(cs, i * f, f, f);
                    ci = i;
                    break;
                }
            }

            if (string.IsNullOrEmpty(c))
            {
                c = ss(cs, cc - f, f);
                ch = chs;
            }
            return si(c);
        }

        public string si(string c)
        {
            return $"https://img{ ss(c, 4, 2)}.8comic.com/{ss(c, 6, 1)}/{ti}/{ss(c, 0, 4)}/{nn(p)}_{ss(c, mm(p) + 10, 3, f)}.jpg";
        }

        public string ss(string a, int b, int c, int? d = null) // 自帶初始值
        {
            string e = a.Substring(b, c);

            return (d == null) ? Regex.Replace(e, "[a-z]*", "") : e;  //三元運算

        }

        public string nn(int n)
        {
            return n.ToString().PadLeft(3, '0');
        }
        public int mm(int p)
        {
            return ((int)((p - 1) / 10) % 10) + (((p - 1) % 10) * 3);
        }
    }
}
